package com.example.waypal;import android.location.Location;public class Trip {	final String id;	POI pois;		public Trip() {		this.id = "trip" + (int)(Math.random() * 9999);	}		public class POI {		final String name;		final Location loc;		final String summary;				public POI(String name, Location loc, String summary) {			this.name = name;			this.loc = loc;			this.summary = summary;		}	}}